import backgroundImage from '../../assests/images/hero_image.png';
export const HeroStyles = {

    heroImage:{
        backgroundImage: `url(${backgroundImage})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        width: '100vw',
        height: '100vh'
    }
};
